mapper.select:
/* AdmAgrStiplMngMapper.selectAgrMngDetail */
SELECT '컬럼정보' AS COL
  FROM irsuser.TEMP_TABLE
 WHERE CONDITION = #{condition}

mapper.insert:
/* AdmAgrStiplMngMapper.insertAgrMng */
INSERT
  INTO irsuser.TEMP_TABLE
	 ( COL
	 , USE_YN
	 , RGTR_ID
	 )
VALUES
	 ( #{col}
	 , #{useYn}
	 , #{sessionUserId}
	 )

mapper.update:
/* AdmAgrStiplMngMapper.updateAgrMng */
UPDATE irsuser.TEMP_TABLE
   SET COL = #{col}
     , MDFR_ID = #{sessionUserId}
     , MDFCN_DT = CURRENT_TIMESTAMP
 WHERE CONDITION = #{condition}

