mapper.select:
/* AdmComCdMapper.selectComGrpCdListCnt */ SELECT COUNT(*) FROM irsuser.GROUP_CD_BSC WHERE 1 = 1 AND DEL_YN = 'N'
AND GROUP_CD LIKE CONCAT('%', #{groupCd}, '%')
AND GROUP_CD_NM LIKE CONCAT('%', #{groupCdNm}, '%')
AND GROUP_CD_ENG_NM LIKE CONCAT('%', #{groupCdEngNm}, '%')
AND USE_YN = #{useYn}

/* AdmComCdMapper.selectComGrpCdList */ SELECT GROUP_CD /* 그룹코드 */ , GROUP_CD_NM /* 그룹코드명 */ , GROUP_CD_ENG_NM /* 그룹코드영문명 */ , GROUP_CD_EXPLN /* 그룹코드설명 */ , USE_YN /* 사용여부 */ FROM irsuser.GROUP_CD_BSC WHERE 1 = 1 AND DEL_YN = 'N'
AND GROUP_CD LIKE CONCAT('%', #{groupCd}, '%')
AND GROUP_CD_NM LIKE CONCAT('%', #{groupCdNm}, '%')
AND GROUP_CD_ENG_NM LIKE CONCAT('%', #{groupCdEngNm}, '%')
AND USE_YN = #{useYn}
ORDER BY GROUP_CD ASC

mapper.insert:
/* AdmComCdMapper.insertComGrpCd */
INSERT
  INTO irsuser.GROUP_CD_BSC
	 ( GROUP_CD
	 , GROUP_CD_NM
	 , GROUP_CD_ENG_NM
	 , GROUP_CD_EXPLN
	 , CD_REG_LMT_CNT
	 , USE_YN
	 , RGTR_ID
	 )
VALUES
	 ( #{groupCd}
	 , #{groupCdNm}
	 , #{groupCdEngNm}
	 , #{groupCdExpln}
	 , #{cdRegLmtCnt}
	 , #{useYn}
	 , #{sessionUserId}
	 )

mapper.update:
/* AdmComCdMapper.updateComGrpCd */
UPDATE irsuser.GROUP_CD_BSC
   SET GROUP_CD_NM = #{groupCdNm}
     , GROUP_CD_ENG_NM = #{groupCdEngNm}
     , GROUP_CD_EXPLN = #{groupCdExpln}
     , CD_REG_LMT_CNT = #{cdRegLmtCnt}
     , USE_YN = #{useYn}
     , MDFR_ID = #{sessionUserId}
     , MDFCN_DT = CURRENT_TIMESTAMP
 WHERE GROUP_CD = #{groupCd}

/* AdmComCdMapper.deleteComGrpCd */
UPDATE irsuser.GROUP_CD_BSC
   SET DEL_YN = 'Y'
     , MDFR_ID = #{sessionUserId}
     , MDFCN_DT = CURRENT_TIMESTAMP
 WHERE GROUP_CD = #{groupCd}

mapper.select:
/* AdmComCdMapper.selectComCdListCnt */ SELECT COUNT(*) FROM irsuser.CD_DTL C INNER JOIN irsuser.GROUP_CD_BSC G ON G.GROUP_CD = C.GROUP_CD AND G.GROUP_CD = #{groupCd} WHERE 1 = 1 AND C.DEL_YN = 'N' AND G.DEL_YN = 'N'
AND CD LIKE CONCAT('%', #{cd}, '%')
AND CD_NM LIKE CONCAT('%', #{cdNm}, '%')
AND CD_ENG_NM LIKE CONCAT('%', #{cdEngNm}, '%')
AND C.USE_YN = #{useYn}

/* AdmComCdMapper.selectComCdList */ SELECT G.GROUP_CD , C.CD , C.CD_NM , C.CD_ENG_NM , C.CD_EXPLN , C.SORT_SNO , C.USE_YN FROM irsuser.CD_DTL C INNER JOIN irsuser.GROUP_CD_BSC G ON G.GROUP_CD = C.GROUP_CD AND G.GROUP_CD = #{groupCd} WHERE 1 = 1 AND C.DEL_YN = 'N' AND G.DEL_YN = 'N'
AND CD LIKE CONCAT('%', #{cd}, '%')
AND CD_NM LIKE CONCAT('%', #{cdNm}, '%')
AND CD_ENG_NM LIKE CONCAT('%', #{cdEngNm}, '%')
AND C.USE_YN = #{useYn}
ORDER BY C.SORT_SNO ASC

/* AdmComCdMapper.selectComCd */
SELECT COUNT(*) AS CNT
  FROM irsuser.CD_DTL
 WHERE GROUP_CD = #{groupCd}
   AND CD = #{cd}
   AND DEL_YN = 'N'

mapper.insert:
/* AdmComCdMapper.insertComCd */
INSERT
  INTO irsuser.CD_DTL
	 ( GROUP_CD
	 , CD
	 , CD_NM
	 , CD_ENG_NM
	 , CD_EXPLN
	 , SORT_SNO
	 , USE_YN
	 , RGTR_ID
	 )
	 (SELECT #{groupCd}
		   , #{cd}
		   , #{cdNm}
		   , #{cdEngNm}
		   , #{cdExpln}
		   , COALESCE(MAX(SORT_SNO), 0)+1
		   , #{useYn}
		   , #{sessionUserId}
		FROM irsuser.CD_DTL
	   WHERE GROUP_CD = #{groupCd}
	 )

mapper.update:
/* AdmComCdMapper.updateComCd */
UPDATE irsuser.CD_DTL
   SET CD_NM = #{cdNm}
     , CD_ENG_NM = #{cdEngNm}
     , CD_EXPLN = #{cdExpln}
     , USE_YN = #{useYn}
     , MDFR_ID = #{sessionUserId}
     , MDFCN_DT = CURRENT_TIMESTAMP
 WHERE GROUP_CD = #{groupCd}
   AND CD = #{cd}

/* AdmComCdMapper.deleteComCd */
UPDATE irsuser.CD_DTL
   SET DEL_YN = 'Y'
     , MDFR_ID = #{sessionUserId}
     , MDFCN_DT = CURRENT_TIMESTAMP
 WHERE GROUP_CD = #{groupCd}
   AND CD = #{cd}
