mapper.update:
/* MnmMapper.updateUpMenuSeCd */
UPDATE irs_user.MENU_BSC
   SET MENU_CL_CD = #{menuClCd}
	 , MDFR_ID     = #{sessionUserId}
	 , MDFCN_DT     = CURRENT_TIMESTAMP
 WHERE MENU_ID = #{upMenuId}

mapper.insert:
/* MnmMapper.insertMenu */

	/* MnmMapper.insertMenu.selectKey */

	SELECT CONCAT( LEFT(#{upMenuId}, 3)
				 , LPAD( CAST(CAST(COALESCE(MAX(SUBSTRING(MENU_ID, (3 + 1), 12)), '0') AS INTEGER) + 1 AS TEXT)
					   , 12 - 3
					   , '0'
				   )
		   )
	FROM irs_user.MENU_BSC


/* MnmMapper.insertMenu */
INSERT
  INTO irs_user.MENU_BSC
     ( MENU_ID
	 , UP_MENU_ID
	 , MENU_CL_CD
	 , MENU_NM
	 , MENU_EXPLN
	 , MENU_URL_ADDR
	 , MENU_LVL
	 , SORT_SEQO
	 , SYS_CL_CD
	 , USE_YN
	 , RGTR_ID
	 )
	 (SELECT #{menuId}
		   , #{upMenuId}
		   , #{menuClCd}
		   , #{menuNm}
		   , #{menuExpln}
		   , #{menuUrlAddr}
		   , #{menuLvl}+1
		   , COALESCE(MAX(SORT_SEQO), 0)+1
		   , #{sysClCd}
		   , #{useYn}
		   , #{sessionUserId}
		FROM irs_user.MENU_BSC
	   WHERE UP_MENU_ID = #{upMenuId}
	 )

mapper.update:
/* MnmMapper.updateMenu */
UPDATE irs_user.MENU_BSC
   SET MENU_NM   = #{menuNm}
	 , MENU_EXPLN = #{menuExpln}
	 , MENU_URL_ADDR  = #{menuUrlAddr}
	 , USE_YN    = #{useYn}
	 , MDFR_ID    = #{sessionUserId}
	 , MDFCN_DT    = CURRENT_TIMESTAMP
 WHERE UP_MENU_ID = #{upMenuId}
   AND MENU_ID = #{menuId}
