mapper.select:
/* UrmMapper.selectUserRoleList */ SELECT UR.ROLE_ID , UR.USER_ID , UR.USER_ROLE_SN , U.USER_CL_CD , U.USER_NM , U.LGN_ID FROM irs_user.USER_ROLE_DTL UR INNER JOIN irs_user.ROLE_BSC R ON R.ROLE_ID = UR.ROLE_ID AND R.SYS_CL_CD = #{sysClCd} AND R.ROLE_ID = #{roleId} AND R.USE_YN = 'Y' AND R.DEL_YN = 'N' AND UR.DEL_YN = 'N' INNER JOIN irs_user.USER_BSC U ON U.USER_ID = UR.USER_ID AND U.DEL_YN = 'N' WHERE 1 = 1
AND U.USER_CL_CD = #{userClCd}
ORDER BY UR.USER_ROLE_SN DESC

mapper.insert:

	/* UrmMapper.insertUserRole.selectKey */
	SELECT COALESCE(MAX(USER_ROLE_SN), 0) + 1 FROM irs_user.USER_ROLE_DTL

/* UrmMapper.insertUserRole */
INSERT
  INTO irs_user.USER_ROLE_DTL
     ( USER_ID
     , ROLE_ID
     , USER_ROLE_SN
     , RGTR_ID
     )
VALUES
     ( #{userId}
     , #{roleId}
     , #{userRoleSn}
     , #{sessionUserId}
     )
