AND B.APLY_CL_CD = #{srhAplyClCd}
AND B.BIZ_FORM_CD = #{srhBizFormCd}
AND (B.BIZ_ORGT_NM LIKE CONCAT('%', #{srhBizNm}, '%') OR B.BIZ_KRN_NM LIKE CONCAT('%', #{srhBizNm}, '%'))
AND B.PRCS_TYPE_CD = 'PTC0001' AND B.PRCS_TYPE_CD = 'PTC0002' AND B.PRCS_TYPE_CD = 'PTC0003' AND B.PRCS_TYPE_CD IN ('PTC0004', 'PTC0005') AND B.PRCS_TYPE_CD IN ('PTC0006', 'PTC0007') AND B.PRCS_TYPE_CD = 'PTC0008' AND B.PRCS_TYPE_CD = 'PTC0010' AND B.PRCS_TYPE_CD = 'PTC0011' AND B.PRCS_TYPE_CD = 'PTC0012' AND B.PRCS_TYPE_CD = 'PTC0013' AND B.PRCS_TYPE_CD = 'PTC0014' AND B.PRCS_TYPE_CD = 'PTC0015' AND B.PRCS_TYPE_CD = 'PTC0016' AND B.PRCS_TYPE_CD = 'PTC0017' AND B.PRCS_TYPE_CD = 'PTC0018' AND B.PRCS_TYPE_CD IN ('PTC0019', 'PTC0020') AND B.PRCS_TYPE_CD IN ('PTC0021', 'PTC0022') AND B.PRCS_TYPE_CD = 'PTC0023' AND B.PRCS_TYPE_CD = 'PTC0024' AND B.PRCS_TYPE_CD = 'PTC0025' AND B.PRCS_TYPE_CD IN ('PTC0027', 'PTC0028', 'PTC0029', 'PTC0030', 'PTC0031', 'PTC0032') AND B.PRCS_TYPE_CD = 'PTC0026'
AND B.BIZ_FLD_CD = #{srhBizFldCd}
AND B.BIZ_DTLS_FLD_CD = #{srhBizDtlsFldCd}
AND B.BIZ_TYPE_CD = #{srhBizTypeCd}
AND B.BIZ_SCAL_CD = #{srhBizScalCd}
AND B.CMPTNC_INST_MNG_NO = #{srhInstMngNo}
AND COALESCE(B.ADD_DATA_TYPE_CD, '') != ''
AND EXISTS ( SELECT BIZ_MNG_NO FROM IRSUSER.IRB_EXCR_DTL WHERE 1=1 AND BIZ_MNG_NO = B.BIZ_MNG_NO AND BIZ_DGR = B.BIZ_DGR AND INST_MGNO = #{auth.sInstMgno} AND DEL_YN = 'N' UNION SELECT BIZ_MNG_NO FROM IRSUSER.IRB_SCSN_EXCR_DTL WHERE 1=1 AND BIZ_MNG_NO = B.BIZ_MNG_NO AND BIZ_DGR = B.BIZ_DGR AND WPMP_INST_MGNO = #{auth.sInstMgno} AND DEL_YN = 'N' )
AND B.BIZ_MNG_NO IN ( SELECT DISTINCT BIZ_MNG_NO FROM IRSUSER.IRB_MHDLG_DTL WHERE 1=1 AND MHDLG_MGNO IN ( SELECT MHDLG_MGNO FROM IRSUSER.MHDLG_DTL WHERE 1=1 AND (MHDLG_ORGT_NM LIKE CONCAT('%', #{srhMhdlg}, '%') OR MHDLG_KRN_NM LIKE CONCAT('%', #{srhMhdlg}, '%')) ) )
AND A.DEL_YN = 'N' AND B.DEL_YN = 'N'

