SELECT '' AS CHK ,(COUNT(*) OVER()) - (U.RN-1) AS NO ,U.BIZ_MNG_NO ,CAST(U.BIZ_DGR AS TEXT) AS BIZ_DGR ,U.DGR_REG_RSN_CD ,U.BIZ_TYPE_CD ,IRSUSER.FN_GET_CODENM('AGREE_CLAUS_CD', U.AGREE_CLAUS_CD) AS AGREE_CLAUS_CD_NM ,CASE WHEN U.BIZ_TYPE_CD = 'BTC0003' THEN '[' || ( SELECT T.BIZ_ORGT_NM FROM ( SELECT BIZ_ORGT_NM, RANK() OVER(PARTITION BY BIZ_MNG_NO ORDER BY BIZ_DGR DESC) AS RN FROM IRSUSER.IRB_PRGRM_DTL WHERE 1=1 AND BIZ_MNG_NO = U.PRGRM_MNG_NO ) T WHERE T.RN = 1 ) || '] ' || U.BIZ_ORGT_NM ELSE U.BIZ_ORGT_NM END AS BIZ_ORGT_NM ,CASE WHEN U.BIZ_TYPE_CD = 'BTC0003' THEN '[' || ( SELECT T.BIZ_KRN_NM FROM ( SELECT BIZ_KRN_NM, RANK() OVER(PARTITION BY BIZ_MNG_NO ORDER BY BIZ_DGR DESC) AS RN FROM IRSUSER.IRB_PRGRM_DTL WHERE 1=1 AND BIZ_MNG_NO = U.PRGRM_MNG_NO ) T WHERE T.RN = 1 ) || '] ' || U.BIZ_KRN_NM ELSE U.BIZ_KRN_NM END AS BIZ_KRN_NM ,IRSUSER.FN_GET_CODENM('BIZ_TYPE_CD', U.BIZ_TYPE_CD) AS BIZ_TYPE_CD_NM ,IRSUSER.FN_GET_CODENM('CERT_VLD_TYPE_CD', U.CERT_VLD_TYPE_CD) AS CERT_VLD_TYPE_CD_NM ,U.CERT_VLD_BGNG_YMD ,U.CERT_VLD_END_YMD ,COALESCE((SELECT BZENT_NM FROM IRSUSER.INST_BSC WHERE INST_MGNO = U.CMPTNC_INST_MNG_NO),'-') AS BZENT_NM ,IRSUSER.FN_GET_CODENM('PRCS_TYPE_CD', U.PRCS_TYPE_CD) AS PRCS_TYPE_CD_NM ,CASE WHEN COALESCE(U.ADD_DATA_TYPE_CD, '') = 'ADT0001' THEN '자료요청' WHEN COALESCE(U.ADD_DATA_TYPE_CD, '') = 'ADT0002' THEN '자료제출' WHEN COALESCE(U.ADD_DATA_TYPE_CD, '') = 'ADT0003' THEN '자료확인(반려)' WHEN COALESCE(U.ADD_DATA_TYPE_CD, '') = 'ADT0004' THEN '자료확인(승인)' ELSE '-' END AS ADD_DATA_NM ,CASE WHEN U.RTADTI_SCSN_PSBLTY_YN = 'Y' THEN '1' ELSE '0' END AS BTN_STS ,CASE WHEN U.RTADTI_SCSN_PSBLTY_YN = 'Y' THEN '선택' ELSE '선택불가' END AS BTN_NM FROM ( SELECT ROW_NUMBER() OVER() AS RN , V.* FROM ( SELECT A.BIZ_MNG_NO, A.RTADTI_SCSN_PSBLTY_YN ,B.BIZ_DGR ,B.AGREE_CLAUS_CD, B.BIZ_ORGT_NM, B.BIZ_KRN_NM, B.BIZ_TYPE_CD ,B.CERT_VLD_TYPE_CD, B.CERT_VLD_BGNG_YMD, B.CERT_VLD_END_YMD, B.DGR_REG_RSN_CD ,B.PRCS_TYPE_CD, B.ADD_DATA_TYPE_CD, B.PRGRM_MNG_NO, B.CMPTNC_INST_MNG_NO FROM IRSUSER.IRB_BSC A INNER JOIN IRSUSER.IRB_DTL B ON A.BIZ_MNG_NO = B.BIZ_MNG_NO AND A.PRCS_DGR = B.BIZ_DGR WHERE 1=1
AND ( B.BIZ_ORGT_NM LIKE '%' || IRSUSER.FN_TRANSFORM_SPECIAL_CHARS(#{srhBizNm}) || '%' OR B.BIZ_KRN_NM LIKE '%' || IRSUSER.FN_TRANSFORM_SPECIAL_CHARS(#{srhBizNm}) || '%' )
