mapper.update:
UPDATE IRSUSER.IRB_BSC SET			 
	 BIZ_CHG_PSBLTY_YN = #{bizChgPsbltyYn}
	,CERT_PRD_UPDT_PSBLTY_YN = #{certPrdUpdtPsbltyYn}
	,CERT_BGNG_YMD_CHG_PSBLTY_YN = #{certBgngYmdChgPsbltyYn}
	,RTADTI_SCSN_PSBLTY_YN = #{rtadtiScsnPsbltyYn}
	,ADD_DATA_DMND_PSBLTY_YN = #{addDataDmndPsbltyYn}
	,DLAY_DCLR_DMND_PSBLTY_YN = #{dlayDclrDmndPsbltyYn}
	,BIZ_RTRCN_DMND_PSBLTY_YN = #{bizRtrcnDmndPsbltyYn}
	,KOFF_DCLR_PSBLTY_YN = #{koffDclrPsbltyYn}
	,MDFR_ID = #{auth.sUserId}
	,MDFCN_DT = CURRENT_TIMESTAMP
	,SCSN_TYPE_CD = SCSN_TYPE_CD
	,SCSN_DATA_DGR = SCSN_DATA_DGR 
	,SCSN_DGR = SCSN_DGR 
WHERE 1=1
AND BIZ_MNG_NO = #{bizMngNo}

UPDATE IRSUSER.IRB_DTL SET
	 DGR_REG_RSN_CD = #{dgrRegRsnCd}			
	,INST_MGNO = #{auth.sInstMgno}
	,USER_ID = #{auth.sUserId}			
	,APLY_CL_CD = #{aplyClCd}			
	,TRSM_YN = #{trsmYn}			
	,APLY_TYPE_CD = #{aplyTypeCd}
	,APLY_YMD = CURRENT_TIMESTAMP
	,PRCS_TYPE_CD = #{prcsTypeCd}
	,ADD_DATA_TYPE_CD = #{addDataTypeCd}
	,ADD_DATA_PRCS_YMD = #{addDataPrcsYmd}			
	,EVL_SN = CAST(#{evlSn} AS NUMERIC)
	,DIC_DLBR_SN = CAST(#{dicDlbrSn} AS NUMERIC)
	,DLBR_SN = #{dlbrSn}
	,DLAY_SN = #{dlaySn}
	,RTRCN_SN = #{rtrcnSn}
	,MDFR_ID = #{auth.sUserId}
	,MDFCN_DT = CURRENT_TIMESTAMP
	,SCSN_GBN_CD = #{scsnGbnCd}
	,SCSN_CN = #{scsnCn}
	,SCSN_DATA_FLMNO = #{scsnDataFlmno}
	,SCSN_BIZ_PLND_FLMNO = #{scsnBizPlndFlmno}
	,SCSN_ETC_DATA_FLMNO = #{scsnEtcDataFlmno}
	,SCSN_BGNG_DGR = CAST(#{scsnBgngDgr} AS NUMERIC) 
WHERE 1=1
AND BIZ_MNG_NO = #{bizMngNo}
AND BIZ_DGR = CAST(#{bizDgr} AS NUMERIC)

UPDATE IRSUSER.IRB_SCSN_EXCR_DTL SET 
	 AGRE_YN = 'Y'
	,AGRE_YMD = TO_CHAR(CURRENT_TIMESTAMP, 'YYYYMMDD')
	,MDFR_ID = #{auth.sUserId}
	,MDFCN_DT = CURRENT_TIMESTAMP
WHERE 1=1
AND BIZ_MNG_NO = #{bizMngNo}
AND BIZ_DGR = CAST(#{bizDgr} AS NUMERIC)
AND WPMP_INST_MGNO = #{auth.sInstMgno}
