/* BizMhdlgAplyMapper.selectMhdlgAplyListCnt */ SELECT COUNT(*) FROM irsuser.MHDLG_BSC MB INNER JOIN irsuser.MHDLG_DTL MD ON MD.MHDLG_MGNO = MB.MHDLG_MGNO AND MD.MHDLG_DGR = MB.PRCS_DGR INNER JOIN irsuser.INST_BSC IB /* 업체 */ ON IB.INST_MGNO = MD.INST_MGNO WHERE MB.DEL_YN = 'N' AND MD.DEL_YN = 'N' AND MD.APLY_CL_CD != 'MAC0004' AND MD.INST_MGNO = (SELECT INST_MGNO FROM irsuser.USER_BSC WHERE USER_ID = #{sUserId})
AND MD.APLY_CL_CD = #{srhAplyClCd}
AND MB.REG_RSN_CD = #{srhMhdlgPrcndTypeCd}
AND MD.PRCS_TYPE_CD = #{srhPrcsTypeCd}
AND COALESCE(MD.ADD_DATA_TYPE_CD, '') != ''
AND MD.CMPTNC_INST_NO = #{srhInstMngNo}
AND MD.MHDLG_ORGT_NM LIKE CONCAT('%', #{srhMhdlgNm}, '%') OR MD.MHDLG_KRN_NM LIKE CONCAT('%', #{srhMhdlgNm}, '%')
AND EXISTS ( SELECT 1 FROM irsuser.MHDLG_FLD_DTL SMFD WHERE SMFD.MHDLG_MGNO = MD.MHDLG_MGNO AND SMFD.MHDLG_DGR = MD.MHDLG_DGR AND SMFD.BIZ_FLD_CD_MGNO = LTRIM(#{srhBizFldCd},'0')
AND SMFD.BIZ_FLD_DTLS_CD_MGNO = #{srhBizDtlsFldCd}
)

/* BizMhdlgAplyMapper.selectMhdlgAplyList */ SELECT '' as CHK , UU.RNN /* 순번 */ , UU.MHDLG_MGNO /* 방법론 관리번호 */ , UU.MHDLG_GROUP_MGNO /* 방법론 관리번호 */ , CAST(UU.MHDLG_DGR AS NUMERIC) /* 방법론 차수 */ , UU.APLY_CL_CD /* 신청구분코드 */ , irsuser.FN_GET_CODENM('MHDLG_APLY_CL_CD', UU.APLY_CL_CD) AS APLY_TYPE_NM /* 신청구분명 */ , UU.AGREE_CLAUS_CD /* 협정조항코드 */ , irsuser.FN_GET_CODENM('AGREE_CLAUS_CD', UU.AGREE_CLAUS_CD) AS AGREE_CLAUS_NM /* 협정조항명 */ , UU.INST_MGNO /* 기관 관리번호 - 신청기관*/ , UU.BZENT_NM /* 기관명 - 신청기관 */ , UU.CMPTNC_INST_NO /* 관할기관번호 - 관장기관 */ , UU.GOV_BZENT_NM /* 관할기관명 - 관장기관 */ , UU.APLY_YMD /* 신청일자 */ , UU.MHDLG_KRN_NM /* 방법론 한글명 */ , UU.MHDLG_ORGT_NM /* 방법론 원문명 */ , UU.MHDLG_UNQNO /* 방법론 고유번호 */ , UU.VLD_BGNG_YMD /* 유효시작일 */ , UU.ADD_DATA_DMND_PSBLTY_YN , UU.PRCS_TYPE_CD /* 처리유형코드 */ , irsuser.FN_GET_CODENM('MHDLG_PRCS_TYPE_CD',UU.PRCS_TYPE_CD) AS PRCS_TYPE_NM /* 처리유형코드명 */ , UU.REG_RSN_CD /* 제정/개정 */ , irsuser.FN_GET_CODENM('MHDLG_PRCND_TYPE_CD',UU.REG_RSN_CD) AS REG_RSN_NM /* 제정/개정 명 */ , UU.ADD_DATA_TYPE_CD /* 추가자료유형코드 */ , CASE WHEN ARRAY_LENGTH(UU.MHDLG_FLD_ARRAY, 1) > 1 THEN ARRAY_TO_STRING(UU.MHDLG_FLD_ARRAY, E'\n') ELSE UU.MHDLG_FLD_ARRAY[1] END AS MHDLG_FLD_LIST_TEXT , CASE WHEN ARRAY_LENGTH(UU.MHDLG_FLD_ARRAY, 1) > 1 THEN ARRAY_TO_STRING(UU.MHDLG_FLD_ARRAY, ' ') ELSE UU.MHDLG_FLD_ARRAY[1] END AS MHDLG_FLD_LIST_TEXT2 , CASE WHEN UU.PRCS_TYPE_CD = 'MPT0010' OR UU.PRCS_TYPE_CD = 'MPT0019' THEN '이의신청' WHEN UU.PRCS_TYPE_CD = 'MPT0003' OR UU.PRCS_TYPE_CD = 'MPT0004' OR UU.PRCS_TYPE_CD = 'MPT0015' OR UU.PRCS_TYPE_CD = 'MPT0016' THEN '문서보완' WHEN UU.PRCS_TYPE_CD = 'MPT0005' OR UU.PRCS_TYPE_CD = 'MPT0006' OR UU.PRCS_TYPE_CD = 'MPT0017' OR UU.PRCS_TYPE_CD = 'MPT0018' THEN '문서재제출' ELSE '조회' END AS BTN_NM , CASE WHEN UU.DATA_DGR > 0 THEN CASE WHEN UU.TYPE_CD = 'DTC0001' THEN '보완' || UU.DATA_DGR || '차' WHEN UU.TYPE_CD = 'DTC0002' THEN '보완(심의)' || UU.DATA_DGR || '차' ELSE '' END ELSE '' END TYPE_CD_NM , CASE WHEN COALESCE(UU.ADD_DATA_TYPE_CD, '') = 'ADT0001' THEN '자료요청' WHEN COALESCE(UU.ADD_DATA_TYPE_CD, '') = 'ADT0002' THEN '조회' WHEN COALESCE(UU.ADD_DATA_TYPE_CD, '') = 'ADT0003' THEN '자료확인(반려)' WHEN COALESCE(UU.ADD_DATA_TYPE_CD, '') = 'ADT0004' THEN '자료확인(승인)' ELSE '-' END AS ADD_DATA_BTN_NM , CAST(COALESCE((SELECT MAX(ADD_DATA_DGR) FROM IRSUSER.MHDLG_ADD_DATA_DTL WHERE MHDLG_MGNO = UU.MHDLG_MGNO), 0) AS TEXT ) AS ADD_DATA_DGR FROM ( SELECT VV.RNN , VV.MHDLG_MGNO , VV.MHDLG_GROUP_MGNO , VV.MHDLG_DGR , VV.INST_MGNO , VV.CMPTNC_INST_NO , VV.APLY_YMD , VV.MHDLG_KRN_NM , VV.MHDLG_ORGT_NM , VV.APLY_CL_CD , VV.AGREE_CLAUS_CD , VV.VLD_BGNG_YMD , VV.REG_RSN_CD , VV.MHDLG_UNQNO , VV.PRCS_TYPE_CD , VV.ADD_DATA_TYPE_CD , VV.ADD_DATA_DMND_PSBLTY_YN , VV.REG_DT , VV.BZENT_NM , VV.TYPE_CD , VV.DATA_DGR , GIB.BZENT_NM AS GOV_BZENT_NM , ( SELECT ARRAY_AGG(X.MHDLG_FLD) FROM ( SELECT CASE WHEN Z.MHDLG_FLD_TOTAL_CNT = 1 THEN Z.MHDLG_FLD /* 1건이면 문자만 출력 */ ELSE '(' || MHDLG_FLD_ROWNUM || ') ' || Z.MHDLG_FLD /* 2건이상이면 앞에 (1) 숫자 붙이기 */ END AS MHDLG_FLD FROM ( SELECT GRP_CD.BIZ_FLD_KORN_NM || ' - ' || DTL_CD.BIZ_DTLS_FLD_KORN_NM AS MHDLG_FLD , ROW_NUMBER() OVER(PARTITION BY MFD.MHDLG_MGNO, MFD.MHDLG_DGR ORDER BY DTL_CD.BIZ_FLD_DTLS_CD_MGNO) AS MHDLG_FLD_ROWNUM , COUNT(*) OVER() MHDLG_FLD_TOTAL_CNT FROM irsuser.MHDLG_FLD_DTL MFD /* 방법론 사업세부분야 */ INNER JOIN irsuser.COM_BIZ_FLD_BSC GRP_CD ON GRP_CD.BIZ_FLD_CD_MGNO = MFD.BIZ_FLD_CD_MGNO INNER JOIN irsuser.COM_BIZ_FLD_DTL DTL_CD ON DTL_CD.BIZ_FLD_DTLS_CD_MGNO = MFD.BIZ_FLD_DTLS_CD_MGNO WHERE MFD.MHDLG_MGNO = VV.MHDLG_MGNO AND MFD.MHDLG_DGR = VV.MHDLG_DGR ) Z ) X ) AS MHDLG_FLD_ARRAY /* 방법론 분야정보 - 배열로 조립하기 */ FROM ( SELECT U.TOTAL_CNT - U.RN + 1 AS RNN , U.MHDLG_MGNO , U.MHDLG_GROUP_MGNO , U.MHDLG_DGR , U.INST_MGNO , U.CMPTNC_INST_NO , U.APLY_YMD , U.MHDLG_KRN_NM , U.MHDLG_ORGT_NM , U.APLY_CL_CD , U.AGREE_CLAUS_CD , U.VLD_BGNG_YMD , U.REG_RSN_CD , U.MHDLG_UNQNO , U.PRCS_TYPE_CD , U.ADD_DATA_TYPE_CD , U.REG_DT , U.BZENT_NM , U.ADD_DATA_DMND_PSBLTY_YN , U.TYPE_CD , U.DATA_DGR FROM ( SELECT ROW_NUMBER() OVER( ORDER BY MB.MHDLG_MGNO DESC) AS RN , COUNT(*) OVER() AS TOTAL_CNT , MB.MHDLG_MGNO , MB.MHDLG_GROUP_MGNO , MD.MHDLG_DGR , MD.INST_MGNO /* 기관관리번호 - 업체명 */ , MD.CMPTNC_INST_NO /* 관할기관번호 - 관장기관 */ , MD.APLY_YMD /* 신청일자 */ , MD.MHDLG_KRN_NM /* 방법론 한글명 */ , MD.MHDLG_ORGT_NM /* 방법론 원문명 */ , MD.APLY_CL_CD /* 신청구분코드 */ , MD.AGREE_CLAUS_CD /* 협정조항코드 */ , MD.VLD_BGNG_YMD /* 유효시작일 */ , MB.REG_RSN_CD /* 제정/개정 */ , MB.MHDLG_UNQNO /* 방법론 고유번호 */ , MD.PRCS_TYPE_CD /* 처리유형코드 */ , MD.ADD_DATA_TYPE_CD /* 추가자료유형코드 */ , MB.ADD_DATA_DMND_PSBLTY_YN , MD.REG_DT /* 등록일자 */ , MB.TYPE_CD , MB.DATA_DGR , IB.BZENT_NM FROM irsuser.MHDLG_BSC MB INNER JOIN irsuser.MHDLG_DTL MD ON MD.MHDLG_MGNO = MB.MHDLG_MGNO AND MD.MHDLG_DGR = MB.PRCS_DGR INNER JOIN irsuser.INST_BSC IB /* 업체 */ ON IB.INST_MGNO = MD.INST_MGNO WHERE MB.DEL_YN = 'N' AND MD.DEL_YN = 'N' AND MD.APLY_CL_CD != 'MAC0004' AND MD.INST_MGNO = (SELECT INST_MGNO FROM irsuser.USER_BSC WHERE USER_ID = #{sUserId})
AND MD.APLY_CL_CD = #{srhAplyClCd}
AND MB.REG_RSN_CD = #{srhMhdlgPrcndTypeCd}
AND MD.PRCS_TYPE_CD = #{srhPrcsTypeCd}
AND MD.CMPTNC_INST_NO = #{srhInstMngNo}
AND COALESCE(MD.ADD_DATA_TYPE_CD, '') != ''
AND MD.MHDLG_ORGT_NM LIKE CONCAT('%', #{srhMhdlgNm}, '%') OR MD.MHDLG_KRN_NM LIKE CONCAT('%', #{srhMhdlgNm}, '%')
AND EXISTS ( SELECT 1 FROM irsuser.MHDLG_FLD_DTL SMFD WHERE SMFD.MHDLG_MGNO = MD.MHDLG_MGNO AND SMFD.MHDLG_DGR = MD.MHDLG_DGR AND SMFD.BIZ_FLD_CD_MGNO = LTRIM(#{srhBizFldCd},'0')
AND SMFD.BIZ_FLD_DTLS_CD_MGNO = #{srhBizDtlsFldCd}
)
) U LIMIT #{pageNavigation.listSize} OFFSET (#{pageNavigation.pageNo} - 1) * #{pageNavigation.listSize}
) VV LEFT OUTER JOIN irsuser.INST_BSC GIB /* 관장기관 */ ON GIB.INST_MGNO = VV.CMPTNC_INST_NO ORDER BY VV.RNN DESC ) UU

