/* BizMhdlgAplyMapper.selectMhdlgAplyExcel */ SELECT UU.RNN AS NO , irsuser.FN_GET_CODENM('MHDLG_APLY_CL_CD', UU.APLY_CL_CD) AS APLY_TYPE_NM /* 신청구분명 */ , irsuser.FN_GET_CODENM('AGREE_CLAUS_CD', UU.AGREE_CLAUS_CD) AS AGREE_CLAUS_NM /* 협정조항명 */ , UU.BZENT_NM /* 기관명 - 신청기관 */ , UU.GOV_BZENT_NM /* 관할기관명 - 관장기관 */ , UU.APLY_YMD /* 신청일자 */ , UU.MHDLG_KRN_NM /* 방법론 한글명 */ , UU.MHDLG_ORGT_NM /* 방법론 원문명 */ , UU.MHDLG_UNQNO /* 방법론 고유번호 */ , UU.VLD_BGNG_YMD /* 유효시작일 */ , irsuser.FN_GET_CODENM('MHDLG_PRCS_TYPE_CD',UU.PRCS_TYPE_CD) AS PRCS_TYPE_NM /* 처리유형코드명 */ , irsuser.FN_GET_CODENM('MHDLG_PRCND_TYPE_CD',UU.REG_RSN_CD) AS REG_RSN_NM /* 제정/개정 명 */ , CASE WHEN ARRAY_LENGTH(UU.MHDLG_FLD_ARRAY, 1) > 1 THEN ARRAY_TO_STRING(UU.MHDLG_FLD_ARRAY, E'\n') ELSE UU.MHDLG_FLD_ARRAY[1] END AS MHDLG_FLD_LIST_TEXT FROM ( SELECT VV.RNN , VV.MHDLG_MGNO , VV.MHDLG_GROUP_MGNO , VV.MHDLG_DGR , VV.INST_MGNO , VV.CMPTNC_INST_NO , VV.APLY_YMD , VV.MHDLG_KRN_NM , VV.MHDLG_ORGT_NM , VV.APLY_CL_CD , VV.AGREE_CLAUS_CD , VV.VLD_BGNG_YMD , VV.REG_RSN_CD , VV.MHDLG_UNQNO , VV.PRCS_TYPE_CD , VV.ADD_DATA_TYPE_CD , VV.ADD_DATA_DMND_PSBLTY_YN , VV.REG_DT , VV.BZENT_NM , VV.TYPE_CD , VV.DATA_DGR , GIB.BZENT_NM AS GOV_BZENT_NM , ( SELECT ARRAY_AGG(X.MHDLG_FLD) FROM ( SELECT CASE WHEN Z.MHDLG_FLD_TOTAL_CNT = 1 THEN Z.MHDLG_FLD /* 1건이면 문자만 출력 */ ELSE '(' || MHDLG_FLD_ROWNUM || ') ' || Z.MHDLG_FLD /* 2건이상이면 앞에 (1) 숫자 붙이기 */ END AS MHDLG_FLD FROM ( SELECT GRP_CD.BIZ_FLD_KORN_NM || ' - ' || DTL_CD.BIZ_DTLS_FLD_KORN_NM AS MHDLG_FLD , ROW_NUMBER() OVER(PARTITION BY MFD.MHDLG_MGNO, MFD.MHDLG_DGR ORDER BY DTL_CD.BIZ_FLD_DTLS_CD_MGNO) AS MHDLG_FLD_ROWNUM , COUNT(*) OVER() MHDLG_FLD_TOTAL_CNT FROM irsuser.MHDLG_FLD_DTL MFD /* 방법론 사업세부분야 */ INNER JOIN irsuser.COM_BIZ_FLD_BSC GRP_CD ON GRP_CD.BIZ_FLD_CD_MGNO = MFD.BIZ_FLD_CD_MGNO INNER JOIN irsuser.COM_BIZ_FLD_DTL DTL_CD ON DTL_CD.BIZ_FLD_DTLS_CD_MGNO = MFD.BIZ_FLD_DTLS_CD_MGNO WHERE MFD.MHDLG_MGNO = VV.MHDLG_MGNO AND MFD.MHDLG_DGR = VV.MHDLG_DGR ) Z ) X ) AS MHDLG_FLD_ARRAY /* 방법론 분야정보 - 배열로 조립하기 */ FROM ( SELECT U.TOTAL_CNT - U.RN + 1 AS RNN , U.MHDLG_MGNO , U.MHDLG_GROUP_MGNO , U.MHDLG_DGR , U.INST_MGNO , U.CMPTNC_INST_NO , U.APLY_YMD , U.MHDLG_KRN_NM , U.MHDLG_ORGT_NM , U.APLY_CL_CD , U.AGREE_CLAUS_CD , U.VLD_BGNG_YMD , U.REG_RSN_CD , U.MHDLG_UNQNO , U.PRCS_TYPE_CD , U.ADD_DATA_TYPE_CD , U.REG_DT , U.BZENT_NM , U.ADD_DATA_DMND_PSBLTY_YN , U.TYPE_CD , U.DATA_DGR FROM ( SELECT ROW_NUMBER() OVER( ORDER BY MB.MHDLG_MGNO DESC) AS RN , COUNT(*) OVER() AS TOTAL_CNT , MB.MHDLG_MGNO , MB.MHDLG_GROUP_MGNO , MD.MHDLG_DGR , MD.INST_MGNO /* 기관관리번호 - 업체명 */ , MD.CMPTNC_INST_NO /* 관할기관번호 - 관장기관 */ , MD.APLY_YMD /* 신청일자 */ , MD.MHDLG_KRN_NM /* 방법론 한글명 */ , MD.MHDLG_ORGT_NM /* 방법론 원문명 */ , MD.APLY_CL_CD /* 신청구분코드 */ , MD.AGREE_CLAUS_CD /* 협정조항코드 */ , MD.VLD_BGNG_YMD /* 유효시작일 */ , MB.REG_RSN_CD /* 제정/개정 */ , MB.MHDLG_UNQNO /* 방법론 고유번호 */ , MD.PRCS_TYPE_CD /* 처리유형코드 */ , MD.ADD_DATA_TYPE_CD /* 추가자료유형코드 */ , MB.ADD_DATA_DMND_PSBLTY_YN , MD.REG_DT /* 등록일자 */ , MB.TYPE_CD , MB.DATA_DGR , IB.BZENT_NM FROM irsuser.MHDLG_BSC MB INNER JOIN irsuser.MHDLG_DTL MD ON MD.MHDLG_MGNO = MB.MHDLG_MGNO AND MD.MHDLG_DGR = MB.PRCS_DGR INNER JOIN irsuser.INST_BSC IB /* 업체 */ ON IB.INST_MGNO = MD.INST_MGNO WHERE MB.DEL_YN = 'N' AND MD.DEL_YN = 'N' AND MD.APLY_CL_CD != 'MAC0004' AND MD.INST_MGNO = (SELECT INST_MGNO FROM irsuser.USER_BSC WHERE USER_ID = #{sUserId})
AND MD.APLY_CL_CD = #{srhAplyClCd}
AND MB.REG_RSN_CD = #{srhMhdlgPrcndTypeCd}
AND MD.PRCS_TYPE_CD = #{srhPrcsTypeCd}
AND MD.CMPTNC_INST_NO = #{srhInstMngNo}
AND COALESCE(MD.ADD_DATA_TYPE_CD, '') != ''
AND MD.MHDLG_ORGT_NM LIKE CONCAT('%', #{srhMhdlgNm}, '%') OR MD.MHDLG_KRN_NM LIKE CONCAT('%', #{srhMhdlgNm}, '%')
AND EXISTS ( SELECT 1 FROM irsuser.MHDLG_FLD_DTL SMFD WHERE SMFD.MHDLG_MGNO = MD.MHDLG_MGNO AND SMFD.MHDLG_DGR = MD.MHDLG_DGR AND SMFD.BIZ_FLD_CD_MGNO = LTRIM(#{srhBizFldCd},'0')
AND SMFD.BIZ_FLD_DTLS_CD_MGNO = #{srhBizDtlsFldCd}
)
) U LIMIT CAST( #{srhTo} AS NUMERIC) OFFSET CAST ( #{srhFrom} AS NUMERIC) -1
) VV LEFT OUTER JOIN irsuser.INST_BSC GIB /* 관장기관 */ ON GIB.INST_MGNO = VV.CMPTNC_INST_NO ORDER BY VV.RNN DESC ) UU

/* BizMhdlgAplyMapper.selectMhdlgBscDgr */
SELECT MAX(A.RVSN_DGR)+1
  FROM irsuser.MHDLG_BSC A
 INNER JOIN irsuser.mhdlg_dtl B
	ON A.MHDLG_MGNO = B.MHDLG_MGNO
 WHERE A.MHDLG_GROUP_MGNO = #{mhdlgGroupMgno}
   AND B.VLD_YN  = 'Y';

/* BizMhdlgAplyMapper.selectMhdlgBscDgr */
SELECT MAX(MHDLG_DGR)+1
  FROM irsuser.mhdlg_dtl
 WHERE MHDLG_MGNO = #{mhdlgMgno}
   AND MHDLG_DGR = CAST(#{mhdlgDgr} AS NUMERIC)

SELECT CASE WHEN C.CMPTNC_INST_NO != '' THEN C.CMPTNC_INST_NO
			WHEN D.MHDLG_INST_MGNO != '' THEN D.MHDLG_INST_MGNO
			ELSE 'GV00000002'
	   END AS CMPTNC_INST_NO 
  FROM irsuser.COM_BIZ_FLD_DTL C
  LEFT OUTER JOIN irsuser.INST_STNG_DTL D
    ON 1=1
 WHERE C.biz_fld_dtls_cd_mgno = #{bizFldDtlsCdMgno}
   AND C.biz_fld_cd_mgno  = #{bizFldCdMgno}
   AND D.inst_mgno = #{instMgno}

SELECT BZENT_NM
  FROM irsuser.INST_BSC
 WHERE INST_MGNO = #{cmptncInstNo}

