mapper.select:
/* MainUserMapper.selectFindUser */
SELECT #{srhType} AS RESULT_TYPE
	 , COALESCE(
				(
					SELECT SUBSTRING(UB.LGN_ID, 1, 2) || LPAD('', LENGTH(UB.LGN_ID) - 2, '*')
					  FROM USER_BSC UB

					 WHERE UB.FLNM = #{srhFlnm}
					   AND UB.EML_ID || '@' || UB.EML_ADDR = #{srhEml}


					 WHERE UB.FLNM = #{srhFlnm}
					   AND UB.LGN_ID = #{srhLgnId}
					   AND UB.EML_ID || '@' || UB.EML_ADDR = #{srhEml}

				)
				, '사용자정보가 존재하지 않습니다.'
		) AS RESULT
	 , COALESCE(
				(
					SELECT UB.USER_ID
					  FROM USER_BSC UB

					 WHERE UB.FLNM = #{srhFlnm}
					   AND UB.EML_ID || '@' || UB.EML_ADDR = #{srhEml}


					 WHERE UB.FLNM = #{srhFlnm}
					   AND UB.LGN_ID = #{srhLgnId}
					   AND UB.EML_ID || '@' || UB.EML_ADDR = #{srhEml}

				)
				, ''
		) AS USER_ID
